import Tile from "./components/Tile";
import Wrapper from "./components/Wrapper";
import Jumbotron from "./components/Jumbotron";
import Counter from "./components/Counter";

import tiles from "./tiles.json";
import React from 'react';
// import logo from './logo.svg';
import './index.css';

class App extends React.Component {
  state = {
    tiles
  }
  componentDidMount() {
    this.random();
  }

  shuffleTiles = () => {
    // const tile = this.state.tiles.filter(tile => tiles.id !== id);
    this.shuffle.resetItems({ tiles });
    this.setState({ tiles })

  }

  random = () => {
    let sortNum = Math.floor(Math.random() * 10);
    let tilesTransformed = this.state.tiles;
    tilesTransformed = tilesTransformed.map(tiles => (
      tiles.sortNum = sortNum
    ));
    this.setState({ tiles: tilesTransformed})
  }

  render() {
    return (
      <div>
      <Counter current={this.state.current} high={this.state.high} />
      <Jumbotron /> 
      <Wrapper>
        {this.state.tiles.map(tiles => (
            // this.random();
                <Tile
                  shuffleTile={this.shuffleTile}
                  id={tiles.id}
                  key={tiles.id}
                  name={tiles.name}
                  image={tiles.image}
                  chosen={tiles.chosen}
                  sortNum={tiles.sortNum}
                />

        ))}
      </Wrapper>
      </div>

    )
  }
}

export default App;
