import React from "react";
import "./style.css";


function Tile(props) {
    return (
    <div className="card">
      <div className="img-container">
        <img alt={props.name} src={props.image} />
        {/* <img src="https://cdn.pixabay.com/photo/2019/07/23/08/42/nature-4356963_1280.jpg" /> */}
      </div>
      <div className="content">
        <strong>Name:</strong> {props.name}
      </div>
      {/* <span onClick={() => props.removeTile(props.id)} className="remove">ùòÖ</span> */}
    </div>
    );
};

export default Tile;
