import Tile from "./components/Tile";
import Wrapper from "./components/Wrapper";
import Jumbotron from "./components/Jumbotron";
import Counter from "./components/Counter";
import _ from "lodash";

import tiles from "./tiles.json";
import React from 'react';
// import logo from './logo.svg';
import './index.css';

class App extends React.Component {
  state = {
    tiles
  }
  componentDidMount() {
    // this.shuffleTiles();
    this.random();
  }

  shuffleTiles = () => {
    // const tile = this.state.tiles.filter(tile => tiles.id !== id);
    this.shuffle.resetItems({ tiles });
    this.setState({ tiles })

  }
  random = () => {
    let randomFunc = () => {
      return Math.floor(Math.random() * 100)
    }
    let tilesTransformed = this.state.tiles;
    tilesTransformed.map(tile => {
      // tile.sortNum = randomFunc();
      tile.sortNum = randomFunc();
      // return (console.log(tile));
    });
    // tilesTransformed.sort();
    let sortedTiles = _.sortBy(tilesTransformed, ['sortNum']);
    // console.log(tilesTransformed);
    // console.log(sortedTiles);

    this.setState({ tiles: sortedTiles})
  }

  //pop and push to re-order array

  render() {
    return (
      <div>
      <Counter current={this.state.current} high={this.state.high} />
      <Jumbotron /> 
      <Wrapper>
        {this.state.tiles.map(tiles => (
            <Tile
            id={tiles.id}
            key={tiles.id}
            name={tiles.name}
            image={tiles.image}
            onClick={this.random}
          />
        ))}
      </Wrapper>
      </div>

    )
  }
}

export default App;
